mapper.select:
SELECT PRODUCT_NO
FROM STORE_STOCK
WHERE PRODUCT_NO = #{PRODUCT_NO}

SELECT STORE_STOCK_EA
FROM STORE_STOCK
WHERE PRODUCT_NO = #{PRODUCT_NO}
AND STORE_NO = #{STORE_NO}

mapper.update:
UPDATE STORE_STOCK
SET 
	STORE_STOCK_EA = #{STORE_STOCK_EA}
WHERE
	STORE_NO = #{STORE_NO}
AND
	PRODUCT_NO = #{PRODUCT_NO}
